        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";
        callback(retObj);
        return;
    } else if (!params['params']['workspace']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Required input 'Workspace / Organization' missing.";

    } else {

        var appinfo = params['appinfo'];
        var userapp = params['userapp'];

        var moment = utils.moment;
        var newTask = {};
        var _ = utils._;

        if (params['params']['due_at']) {
            params['params']['due_at'] = utils.libDate.parseStandardStringToJSDateTime(params['params']['due_at']);
        }

        if (params['params']["due_on"]) {
            params['params']['due_on'] = utils.libDate.parseStandardStringToJSDateTime(params['params']['due_on']);
        }

        _.each(params['params'], function (data, key) {

            if (key.includes('custom_field') && data !== null) {

                newTask.custom_fields = newTask.custom_fields || {}
                newTask.custom_fields[key.replace('custom_field_', '')] = data

            } else if (key === 'projects' && data !== null) {
                newTask.memberships = newTask.memberships || [{}]
                newTask.memberships[0]['project'] = data

            } else if (key === 'section' && data !== null) {

                newTask.memberships = newTask.memberships || [{}]
                newTask.memberships[0]['section'] = data

            } else if ((key === 'followers') && data !== null) {
                let followerArr = data.map(obj => obj.value)
                newTask[key] = followerArr;
            }else if (data !== null) {

                newTask[key] = data;
            }

        });

        var body = {
            data: newTask
        };

        // Generate HTTP Request body

        var fetchedParams = userapp.fetchAuthParams(appinfo);

        var options = {
            // Variables to be modified
            method: 'POST',
            url: '/tasks',

            // Remain same
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: 'https://app.asana.com/api/1.0/',
            json: true,
            body: body
        };

        // Form object to be returned
        retObj['status'] = "SUCCESS";
        retObj['data'] = {
            options: options
        };
    }

    callback(retObj);
    return

}

