function actionParse(hashedParams, hashedUtils, callback) {


    var retObj = {};
    var responseObjArray = [];

    // Input Validation
    if (!hashedParams || !hashedParams['body'] || !hashedParams['response']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Invalid/Missing API Response in actionParse."
        callback(retObj);
        return;
    } else {
        var response = hashedParams['response'];
        var parsed = {};
        var _ = hashedUtils._;

        if (response['statusCode'] === 200) {

            var data = hashedParams["body"]["data"];

            if(hashedParams["step"]["value"] === "step") {
                parsed = parseTask(data);

                retObj["status"] = "SUCCESS";
                retObj["data"] = {
                    parsedValues: parsed
                };
                callback(retObj);
                return;
            }

            var project_id = hashedParams["step"].value;
            var name = hashedParams["step"].name.toLowerCase().trim();
            var found = [];

            if(data && data.length){

                data = data.filter(task => task.name.toLowerCase().trim() === name)
                data.forEach(function(task){

                    if(task.projects && task.projects.length){

                        task.projects.forEach(function(project){
                            if((project.gid || project.id) == project_id){
                                found.push(task);
                            }
                        });

                    }

                });

                if(found && found.length){

                    parsed = parseTask(found[0]);

                    retObj["status"] = "SUCCESS";
                    retObj["data"] = {
                        parsedValues: parsed
                    };

                    callback(retObj);

                }//end of inner if

                else {
                    retObj.status = "SUCCESS";
                    parsed["found"] = "No";
                    retObj["data"] = {
                        parsedValues: parsed
                    }
                    callback(retObj);
                    return
                }

            } //end out outer if checking data and data.length
            else {

                retObj.status = "SUCCESS";
                parsed["found"] = "No";
                retObj["data"] = {
                    parsedValues: parsed
                }
                callback(retObj);
                return

            }//end of outer else

    } else if (hashedParams.response.statusCode == 401) {

            var code = "ERROR_AUTH_REFRESH";
            retObj.status = "ERROR";
            retObj.errorCode = code;
            retObj.error = {
              code,
              message: "error occured due to expiration of token"
            };
            callback(retObj);
            return

  } else if (hashedParams.response.statusCode === 400){

            retObj.status = "ERROR";
            retObj.errorCode = "BAD REQUEST";
            var message = "";
            hashedParams.body.errors.forEach(function(error){
                message += error.message + ", "
            });
            message = message.slice(0, (message.length - 2));
            retObj.error = {
                message: message
            };
            callback(retObj);
            return;

    } else if (hashedParams.response.statusCode === 403){

            retObj.status = "ERROR";
            retObj.errorCode = "Forbidden";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        }  else if (hashedParams.response.statusCode === 404){

            parsed["found"] = "No"

            retObj.status = "SUCCESS";

            retObj["data"] = {
                parsedValues: parsed
            }
            callback(retObj);
            return;

        } else if (hashedParams.response.statusCode === 429){

            retObj.status = "ERROR";
            retObj.errorCode = "Rate Limit Enforced";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        } else {
            retObj['status'] = "ERROR";
            retObj['error'] = {};
            retObj['error']['code'] = "ERROR_UNKNOWN";
            callback(retObj);
            return;

        }
    }

    function parseTask(task) {
        var dates = {
            "created_at": true,
            "modified_at": true,
            "due_at": true,
            "due_on": true,
            "completed_at":true
        };

        var keys = {
            "id": true,
            "name": true,
            "completed": true,
            "parent": true,
            "assignee_status": true,
            "notes": true,
            "gid" : true
        };

        var parsed = {};

        parsed["found"] = "Yes"

        _.each(task, function(data, key){
            if(dates[key]){
                if(data){
                    parsed[key] = hashedUtils.libDate.parseJSDateTimeToStandardString(new Date(data));
                }
            } else if(key === "workspace"){
                if(data){
                    _.each(data, function(wData, wKey){
                        if(wKey === "gid" || wKey === "id" || wKey === "name"){
                            if(wKey == "gid")
                                parsed["workspace_id"] = wData;
                            else
                                parsed["workspace_" + wKey] = wData;
                            }
                    });
                }
            } else if(key === "tags"){

                if(data && data.length){
                    var tags = "";
                    data.forEach(function(tag) {
                        tags += tag.name + ", "
                    });
                    parsed[key] = tags.slice(0, tags.length - 2);
                }

            } else if(key === "parent"){
                if(data && typeof data === 'object' && data.constructor === Object){
                    _.each(data, function(wData, wKey){
                        if(wKey == "gid")
                            parsed["parent_id"] = wData;
                        else
                            parsed["parent_" + wKey] = wData;
                    });
                }
                parsed["parent"] = data
            } else if(key === "assignee"){

                if(data){
                    parsed[key] = data.name;
                    parsed[key + "_id"] = data.gid || data.id;
                }

            } else if(key === "custom_fields" && data instanceof Array){

                        if(data && data.length){

                          data.forEach(field => {

                            if (field.type === 'enum') {
                              parsed[field.gid || field.id] = field.enum_value ? field.enum_value.name : null
                            } else {
                              parsed[field.gid || field.id] = field[field.type + '_value']
                            }

                          })

                        }

            } else if(keys[key]){
                if(data !== null && data !== undefined){
                    if(key == 'gid')
                        parsed['id'] = data
                    else    
                        parsed[key] = data;
                }
            }
        });

        return parsed;
    }

}

