    retObj['error']['code'] = "ERROR_MISSING_PARAM";
    retObj['error']['message'] = "Error: Invalid/Missing API Response in actionParse."
    callback(retObj);
        return;
  } else {
    var response = hashedParams['response'];
    var data = [];

    if (response['statusCode'] === 200) {

      var body = hashedParams["body"];

      if(!(body || {}) || !(body.data || {}) ){
        retObj["status"] = "ERROR";
        retObj["error"] = {};
        retObj["error"]["message"] = "Missing data from body";
        callback(retObj);
        return;
      }

      if (body.data && body.data.length) {
        body.data.forEach(field => {
          if (field.custom_field.type === 'enum') {
            data.push({
              id: 'custom_field_' + (field.custom_field.id || field.custom_field.gid),
              name: field.custom_field.name,
              inputType: "select",
              type: "String",
              fixedValueSource: field.custom_field.enum_options.map(option => {
                return {
                  id: option.id || option.gid,
                  name: option.name
                }
              })
            })
          } else {
            data.push({
              id: 'custom_field_' + (field.custom_field.id || field.custom_field.gid),
              name: field.custom_field.name,
              inputType: "input",
              type: "String"
            })
          }
        })
      }

      retObj["status"] = "SUCCESS";
      retObj["data"] = data;

      callback(retObj);
      return


      // add more error code specific code blocks
    } else if (hashedParams.response.statusCode == 401) {

      var code = "ERROR_AUTH_REFRESH";
      retObj.status = "ERROR";
      retObj.errorCode = code;
      retObj.error = {
        code,
        message: "error occured due to expiration of token"
      };
      callback(retObj);
      return

    } else if (hashedParams.response.statusCode === 402){

      retObj["status"] = "SUCCESS";
      retObj["data"] = [];
      callback(retObj);
      return;

    } else if (hashedParams.response.statusCode === 403){

      retObj.status = "ERROR";
      retObj.errorCode = "Forbidden";
      retObj.error = {
        message: hashedParams.body.errors[0].message
      };
      callback(retObj);
      return;

    }  else if (hashedParams.response.statusCode === 404){

      retObj.status = "ERROR";
      retObj.errorCode = "Not found";
      retObj.error = {
        message: hashedParams.body.errors[0].message
      };
      callback(retObj);
      return;

    } else if (hashedParams.response.statusCode === 429){

      retObj.status = "ERROR";
      retObj.errorCode = "Rate Limit Enforced";
      retObj.error = {
        message: hashedParams.body.errors[0].message
      };
      callback(retObj);
      return;

    } else {
      retObj['status'] = "ERROR";
      retObj['error'] = {};
      retObj['error']['code'] = "ERROR_UNKNOWN";
      callback(retObj);
      return;

    }
  }
}

