    retObj.message = "Userapp or appinfo is missing while requesting in webhook request";
    callback(retObj);
    return;
  }

  if(!(hashedParams["params"]["task_id"])){
    retObj.status = "ERROR";
    retObj.message = "Required Params are missing";
    callback(retObj);
    return;
  }

  if(!(hashedUtils || {}) || !(hashedUtils._ || {} ) || !(hashedUtils.moment || {}) ){
    retObj.status = "ERROR";
    retObj.message = "Please provide utils with underscore and moment";
    callback(retObj);
    return;
  }

  var fetchedParams = hashedParams.userapp.fetchAuthParams(hashedParams.appinfo);

   if(!(fetchedParams["access_token"])){

    retObj.status = "ERROR";
    retObj.message = "cannot fetch access_token from fetchedAuthParams";
    callback(retObj);
    return;

  }//end auth fetch

  var access_token = fetchedParams["access_token"];

  var newTask = {};

  var inputParams = hashedParams.params;

  var _ = hashedUtils._;

  var moment = hashedUtils.moment;
  let sectionUpdate = false;
  let stepUrl=""
  _.each(inputParams, function(data, key){

    if(key.includes('custom_field') && data !== null) {

        newTask.custom_fields = newTask.custom_fields || {}
        newTask.custom_fields[key.replace('custom_field_','')] = data

    }
    else if(key === "due_on"){
        if(data){
            var due_on = hashedUtils.libDate.parseStandardStringToJSDate(data);
            newTask[key] = moment(due_on).format("YYYY-MM-DD");
        }
    }
    else if (key === "due_at"){
        if(data){
            var due_at = hashedUtils.libDate.parseStandardStringToJSDateTime(data);
            newTask[key] = due_at.toISOString();
        }
    }
    else if (key === "assignee"){
        if(data){
            newTask[key] = {
                gid: data
            }
        }
    } else if(key !== "resource_id" && key !== "task_id" ){
        if(data !== null){
            newTask[key] = data;
        }
    }

  });

  var task_id = hashedParams.params.task_id;

  var options = {
      method: 'PUT',

      baseUrl: baseUrl,

      url: path + "/" + task_id,

     headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
        "Authorization": "Bearer " + access_token
      },

      body: {
          data: newTask
      },

      json: true

    };

  retObj.status = "SUCCESS";
  retObj.data = {options: options};
  callback(retObj);
  return

}

