auth.authCallBack:
function authCallBack(hashedParams, hashedUtils, callback) {
  var retObj = {};
  var params = [];
  if (!((hashedParams || {})).queryObj) {
    retObj.status = 'ERROR';
    retObj.error = { message: 'There was an error while connecting to Asana. Please try reconnecting the app to fix this issue. If the issue persists, you can reach out to Asana support' };
    callback(retObj);
    return;
  }
  var queryObj = hashedParams.queryObj;
  if (!(hashedUtils || {}).oauth2) {
    retObj.status = 'ERROR';
    retObj.error = {
      message: 'There was an error while connecting with Asana. Please get in touch with us and we\'ll help you set it up.'
    };
    callback(retObj);
    return;
  }
  //var oauth2 = hashedUtils.oauth2;
  var appInfo = hashedParams.appInfo;
  var oauth2 = hashedUtils.oauth2({
    clientID: appInfo.auth.authParams.clientID,
    clientSecret: appInfo.auth.authParams.clientSecret,
    grant_type: "authorization_code",
    site: appInfo.auth.authParams.site,
    tokenPath: appInfo.auth.authParams.tokenPath,
    authorizationPath: appInfo.auth.authParams.authorizationPath
  });

  if (!hashedParams.queryObj.code) {
    retObj.status = 'ERROR';
    retObj.error = { message: 'There was an error while connecting to Asana. Please try reconnecting the app to fix this issue. If the issue persists, you can reach out to Asana support' };
    callback(retObj);
    return;
  }
  var code = queryObj.code;
  oauth2.authCode.getToken({
    code: code,
    redirect_uri: appInfo.auth.authParams.redirect_uri
  }, saveToken);

  function saveToken(ERROR, result) {
    if (ERROR) {
      retObj.status = 'ERROR';
      retObj.error = { message: 'Access Token ERROR' + ERROR };
      callback(retObj);
      return;
    } else {

      if (result && result['access_token'] && result['refresh_token']) {
        params.push({ 'id': 'access_token', value: result['access_token'] });
        params.push({ 'id': 'refresh_token', value: result['refresh_token'] });
        retObj.status = 'SUCCESS';
        retObj.data = { oauth2Info: result, params: params };
        callback(retObj);
        return;
      } else {
        retObj.status = 'ERROR';
        retObj.error = { message: 'Cannot retrieve all the required tokens ' };
        callback(retObj);
        return;
      }
    }
  }
}

