function authRefreshRequest(hashedParams, hashedUtils, callback) {
  var retObj = {};
  if(!(hashedParams || {}).userapp || !(hashedParams || {}).appinfo){
    retObj.status = "ERROR";
    retObj.error = {message :"Userapp or appinfo is missing while requesting in dynamicValueSourceRequest"};
    callback(retObj);
