  if(!((((hashedParams || {}).appinfo || {}).auth || {}).authParams || {}).clientSecret){
    retObj.status = "ERROR";
    retObj.error ={message: "appInfo is missing clientId"};
    callback(retObj);
    return;
  }
  var refresh_token = _.findWhere(hashedParams.userapp.params, {id: 'refresh_token'});
  if(!refresh_token || !refresh_token.value){
    retObj.status = "ERROR";
    retObj.error ={message: "refresh token is missing in userApp"};
    callback(retObj);
    return;
  }
  var options = {
    url: 'https://app.asana.com/-/oauth_token',
    method: 'POST',
    headers: [
      {
        name: 'content-type',
        value: 'application/x-www-form-urlencoded'
      }
    ],
    form: {
      client_id: hashedParams.appinfo.auth.authParams['clientID'],
      client_secret: hashedParams.appinfo.auth.authParams['clientSecret'],
      refresh_token: refresh_token.value,
      grant_type: 'refresh_token',
      redirect_uri: hashedParams.appinfo.auth.authParams.redirect_uri
    }
  };
  retObj.status = "SUCCESS";
  retObj.data = {options: options};
  callback(retObj);
  return
}

