    callback(retObj);
    return;
  }
}

source.webhookTriggerParse:
function webhookTriggerParse(hashedParams, hashedUtils, callback){

    var retObj = {};

    var _ = hashedUtils._;

     if(hashedParams.request.headers["x-hook-secret"] && _.isEmpty(hashedParams.request.body)){

        retObj.status = "SKIP";
        retObj.message = "Handshake";
        retObj.response = {};
        retObj.response.headers = {
            "x-hook-secret": hashedParams.request.headers["x-hook-secret"]
        };

        callback(retObj);
        return;

    } else if (hashedParams['body'].events.length) {
      var events = hashedParams.body.events

      var removedStories = events
        .filter(event => (event.resource || {}).resource_type === 'story' && event.action === 'removed')
      var addedStories = events
        .filter(event => (event.resource || {}).resource_type === 'story' && event.action === 'added')
        .filter(aEvent => !removedStories.find(rEvent => rEvent.resource === aEvent.resource))

      if (addedStories.length) {
        retObj.data = {objects: addedStories}
        retObj.status = 'SUCCESS'
        callback(retObj)
      } else {
        retObj.status = 'SKIP'
        retObj.message = 'Unwanted Resource'
        callback(retObj)
      }
  
    } else if(hashedParams["body"].events.length === 0) {

        retObj.status = "SKIP";
        retObj.message = "TEST PING";

        callback(retObj);
        return;

    } else {
        retObj.status = "ERROR";
        retObj.error = {
            message: "Error Occured"
        }
        callback(retObj);
        return
    }

}

