function webhookTriggerParse(hashedParams, hashedUtils, callback){
 
    var retObj = {};

    var _ = hashedUtils._;

     if(hashedParams.request.headers["x-hook-secret"] && _.isEmpty(hashedParams.request.body)){

        retObj.status = "SKIP";
        retObj.message = "Handshake";
        retObj.response = {};
        retObj.response.headers = {
            "x-hook-secret": hashedParams.request.headers["x-hook-secret"]
        };

        callback(retObj);
        return;

    } else if (hashedParams['body'].events.length) {
      var events = hashedParams.body.events
      var deletedTasks = events
        .filter(event => event.resource.resource_type === 'task' && event.action === 'deleted')

      var changedTasks = events
        .filter(event => event.resource.resource_type === 'task' && event.action === 'changed')
        .filter(cEvent => !deletedTasks.find(dEvent => dEvent.resource.gid === cEvent.resource.gid))

      var uniqueTasks = []
      var uniqueMap = {}

      changedTasks.forEach(task => {
        if (!uniqueMap[task.resource.gid]) {
          uniqueTasks.push(task)
          uniqueMap[task.resource.gid] = true
        }
      })

      if (uniqueTasks.length) {
        retObj.data = {objects: uniqueTasks}
        retObj.status = 'SUCCESS'
        callback(retObj)
      } else {
        retObj.status = 'SKIP'
        retObj.message = 'Unwanted Resource'
        callback(retObj)
      }

    } else if(hashedParams["body"].events.length === 0) {

        retObj.status = "SKIP";
        retObj.message = "TEST PING";

        callback(retObj);
        return;

    } else {
        retObj.status = "ERROR";
        retObj.error = {
            message: "Error Occured"
        }
        callback(retObj);
        return
    }

}

