function webhookObjectParse(hashedParams, hashedUtils, callback) {

    var retObj = {};
    var _ = hashedUtils._;

    // Input Validation
    if (!hashedParams || !hashedParams['body'] || !hashedParams['response']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Invalid/Missing API Response in webhookObjectRequest."

    } else {
        var response = hashedParams['response'];
        var data = [];

        if (response['statusCode'] === 200) {

            var body = hashedParams["body"];

            if (!(body.data.completed)) {
                retObj.status = "SUCCESS";
                retObj.data = [];
                callback(retObj);
                return;
            }

            if (body.data && body.data.modified_at && body.data.completed_at) {
               
                let lastModified = hashedUtils.moment(body.data.modified_at).unix()
                let completedAt = hashedUtils.moment(body.data.completed_at).unix()
                if (lastModified > completedAt) {
                  
                    retObj.status = "SUCCESS";
                    retObj.data = [];
                    callback(retObj);
                    return;
                }
            }

            var params = {};

            var dates = {
                "created_at": true,
                "modified_at": true,
                "due_on": true,
                "completed_at": true
            };

            var keys = {
                "id": true,
                "name": true,
                "completed": true,
                "parent": true,
                "assignee_status": true,
                "notes": true,
                "gid" : true
            };

            _.each(body.data, function (data, key) {
                if (dates[key]) {
                    if (data) {
                        params[key] = hashedUtils.libDate.parseJSDateTimeToStandardString(new Date(data));
                    }
                } else if(key === "workspace"){
                    if(data){
                        _.each(data, function(wData, wKey){
                            if(wKey == "gid")
                                params["workspace_id"] = wData;
                            else
                                params["workspace_" + wKey] = wData;
                        });
                    }
                } else if (key === "tags") {

                    if (data && data.length) {
                        var tags = "";
                        data.forEach(function (tag) {
                            tags += tag.name + ", "
                        });
                        params[key] = tags.slice(0, tags.length - 2);
                    }

                } else if (key === "assignee") {

                    if (data) {
                        params[key] = data.name;
                        params[key + "_id"] = data.gid || data.id;
                    }

                } else if (key === "custom_fields" && data instanceof Array) {

                    if (data && data.length) {

                        data.forEach(field => {

                        if (field.type === 'enum') {
                          params[field.gid || field.id] = field.enum_value ? field.enum_value.name : null
                        } else {
                          params[field.gid || field.id] = field[field.type + '_value']
                        }

                        })

                    }

                }else if(key === "parent"){
                    if(data && typeof data === 'object' && data.constructor === Object){
                        _.each(data, function(wData, wKey){
                            if(wKey == "gid")
                                params["parent_id" ] = wData;
                            else
                                params["parent_" + wKey] = wData;
                        });
                    }
                    params["parent"] = data
                }else if(key === "projects") {

                    if(data instanceof Array && data.length) {
                        params['projectIds'] = data.map(pro => pro.gid || pro.id).join()
                        params['projectNames'] = data.map(pro => pro.name).join()
                    }
                    
                } else if(keys[key]){
                    if(data !== null && data !== undefined){
                        if(key == 'gid')    
                            params["id"] = data;
                        else
                            params[key] = data;
                    }
                }
            });

            retObj.status = "SUCCESS";
            retObj.data = params;

            callback(retObj);
            return;

        } else if (hashedParams.response.statusCode == 401) {

            var code = "ERROR_AUTH_REFRESH";
            retObj.status = "ERROR";
            retObj.errorCode = code;
            retObj.error = {
                code,
                message: "error occured due to expiration of token"
            };
            callback(retObj);
            return

        } else if (hashedParams.response.statusCode === 403) {

            retObj.status = "ERROR";
            retObj.errorCode = "Forbidden";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        } else if (hashedParams.response.statusCode === 404) {

            retObj.status = "ERROR";
            retObj.errorCode = "Not found";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        } else if (hashedParams.response.statusCode === 429) {

            retObj.status = "ERROR";
            retObj.errorCode = "Rate Limit Enforced";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        } else {
            retObj['status'] = "ERROR";
            retObj['error'] = {};
            retObj['error']['code'] = "ERROR_UNKNOWN";

        }
    }
    callback(retObj);
    return
}

