source.webhookTriggerParse:
function webhookTriggerParse(hashedParams, hashedUtils, callback){
    
    var retObj = {};

    var _ = hashedUtils._;

     if(hashedParams.request.headers["x-hook-secret"] && _.isEmpty(hashedParams.request.body)){

        retObj.status = "SKIP";
        retObj.message = "Handshake";
        retObj.response = {};
        retObj.response.headers = {
            "x-hook-secret": hashedParams.request.headers["x-hook-secret"]
        };

        callback(retObj);
        return;

    } else if(hashedParams["body"].events.length) {

        var story = null;

        var task_id = _.findWhere(hashedParams.workflow.trigger.params, {
            id: "task_id"
        });

        task_id = task_id ? task_id.value : null;

        hashedParams.body.events.forEach(function(event){
            if(event.resource.resource_type === "story" && event.action === "added" && task_id){
                if(event.parent.gid == task_id) {
                    story = event;
                }
                
            } else if (event.resource.resource_type === "story" && event.action === "added") {
                story = event;
            }
        });

        if(story){

            retObj.data = {response: story};
            retObj.status = "SUCCESS";
            callback(retObj);
            return
            
        } else{

            retObj.status = "SKIP";
            retObj.message = "Unwanted Resource"; 
            callback(retObj);
            return;
        }        
        
    } else if(hashedParams["body"].events.length === 0) {

        retObj.status = "SKIP";
        retObj.message = "TEST PING";

        callback(retObj);
        return;

    } else {
        retObj.status = "ERROR";
        retObj.error = {
            message: "Error Occured"
        }
        callback(retObj);
        return
    }

}

