        retObj['error']['message'] = "Error: Invalid/Missing API Response in webhookObjectRequest."

    } else {
        var response = hashedParams['response'];
        var data = [];

        if (response['statusCode'] === 200) {

            var body = hashedParams["body"];

            if(body.data.type !== "comment"){
                retObj.status = "SUCCESS";
                retObj.data = [];
                callback(retObj);
                return;
            }

            var params = {};

            var dates = {
                "created_at": true
            };

            var keys = {
                text: true,
                type: true,
                source: true,
                id: true,
                gid : true
            };

            _.each(body.data, function(data, key){
                if(dates[key]){
                    if(data){
                        params[key] = hashedUtils.libDate.parseJSDateTimeToStandardString(new Date(data));
                    }
                } else if(key === "created_by"){
                    if(data){
                        _.each(data, function(wData, wKey){
                            if(wKey == 'gid')
                                params["created_by_id"] = wData;
                            else
                                params["created_by_" + wKey] = wData;
                        });
                    }
                } else if(key === "target"){

                    if(data){
                        _.each(data, function(tData, tKey){
                            if(tKey === "id" || tKey === "gid"){
                                if(tData){
                                    params["target_id"] = tData;
                                }
                            }
                        });
                    }

                } else if(keys[key]){
                    if(data !== null && data !== undefined){
                        if(key == 'gid')    
                            params["id"] = data;
                        else
                            params[key] = data;
                    }
                }
            });

            retObj.status = "SUCCESS";
            retObj.data = params;

            callback(retObj);
            return;

        } else if (hashedParams.response.statusCode == 401) {

