function objectParse(params, utils, callback) {

    var retObj = {};
    if (!params || !params.response || !params.body) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'response or body to parse is missing'
            },
            status: 'ERROR'
        })
    }
    if (params.response.statusCode === 200) {
        
        var parsed = {};
        var _ = utils._;

        var body = JSON.parse(params.body);

            var dates = {
                "created_at": true,
                "modified_at": true
            };

            var keys = {
                "id": true,
                "name": true,
                "archived": true,
                "notes": true,
                "gid": true
            };

            _.each(body.data, function(data, key){

                if(dates[key]){
                    if(data){
                        parsed[key] = utils.libDate.parseJSDateTimeToStandardString(new Date(data));
                    }
                } else if(keys[key]){
                    if(data !== null && data !== undefined){
                        if(key == 'gid')
                            parsed['id'] = data
                        else    
                            parsed[key] = data;
                    }
                }
            });

            retObj.status = "SUCCESS";
            retObj.data = {
                newObject: {
                    response: {
                        params: parsed
                    }
                },
                hash: {
                    id: body.data.gid || body.data.id
                }
            }

            callback(retObj);
            return;

    } else {
        let code
        if (params.response.statusCode === 401) {
            code = 'ERROR_AUTH_REFRESH'
        } else if (params.response.statusCode === 400) {
            code = 'ERROR_MISSING_PARAM'
        } else if (params.response.statusCode === 403 || params.response.statusCode === 429) {
            code = 'ERROR_RATE_LIMIT'
        } else {
            code = 'ERROR_UNKNOWN'
        }
        return callback({
            error: {
                code,
                message: body.error || params.response.statusCode
            },
            errorCode: code,
            status: 'ERROR'
        })
    }
}

