function objectRequest(params, utils, callback) {

    if (!params || !params.userapp || !params.appinfo) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'Userapp or appinfo is missing'
            },
            status: 'ERROR'
        })
    }
    if (!params.parsedData || !params.parsedData.data) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'pass parsed Data from pollParse'
            },
            status: 'ERROR'
        })
    }
    const authParams = params.userapp.fetchAuthParams(params.appinfo)
    if (!authParams || !authParams.access_token) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'cannot fetch access_token from fetchAuthParams'
            },
            status: 'ERROR'
        })
    }
    const url = 'https://app.asana.com/api/1.0/projects'
    const parsedData = params.parsedData.data.objects || []
    const options = parsedData
        .filter(p => p && p.gid)
        .map(p => ({
            headers: {
                Authorization: `Bearer ${authParams.access_token}`
            },
            method: 'GET',
            url: `${url}/${p.gid}`
        }))

    return callback({
        params: JSON.stringify(params),
        status: 'SUCCESS',
        message: 'Built options for request',
        data: { options }
    })
}

