function webhookRegisterParse(hashedParams, hashedUtils, callback) {
  var retObj = {};

  if(!(hashedParams || {}).response || !(hashedParams || {}).body){
    retObj.status = "ERROR";
    retObj.error = {message: "response or body to parse is missing while requesting in dynamicValueSourceParse"};
    callback(retObj);
    return;
  }

  if (( hashedParams.response.statusCode || {} ) == 200 || ( hashedParams.response.statusCode || {} ) == 201) {
      var parsed = hashedParams.body;
      retObj.status = "SUCCESS";
      retObj.data = {
          webHook:{
            status: 'registered',
            data: parsed.data
          }
        };

      callback(retObj);
      return;

  } else if (hashedParams.response.statusCode == 401) {

            var code = "ERROR_AUTH_REFRESH";
            retObj.status = "ERROR";
            retObj.errorCode = code;
            retObj.error = {
              code,
              message: "error occured due to expiration of token"
            };
            callback(retObj);
            return

  } else if (hashedParams.response.statusCode == 400) {

    var message = hashedParams && hashedParams.body && hashedParams.body.errors.map(err => err.message).join()
    message = message.includes('Unknown object') ? 'Do the selected project and task exist?' : message
    retObj.status = "ERROR";
    retObj.error = { message };
    retObj.data =  { webHook : {} };
    retObj.data.webHook.status = 'Failed';
    retObj.data.webHook.data = { message };
    callback(retObj);

  } else {

    retObj.status = "ERROR";
    retObj.data=  {};
    retObj.error = {message: "error occured with status code:"+hashedParams.response.statusCode};
    retObj.data.webHook = {};
    retObj.data.webHook.status = 'Failed';
    retObj.data.webHook.data = {message: "Error while requesting for webhook registration with status code " + hashedParams.response.statusCode + " body " + JSON.stringify(hashedParams.body)};
    callback(retObj);
    return
  }
}

