source.objectParse:
function objectParse(params, utils, callback) {

    var retObj = {};
    if (!params || !params.response || !params.body) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'response or body to parse is missing'
            },
            status: 'ERROR'
        })
    }
    if (params.response.statusCode === 200) {
        
        var parsed = {};
        var _ = utils._;

        var body = JSON.parse(params.body);

            var dates = {
                "created_at": true,
                "modified_at": true,
                "due_on": true,
                "completed_at":true
            };
            console.log("in object parse")
            var keys = {
                "id": true,
                "name": true,
                "completed": true,
                "parent": true,
                "assignee_status": true,
                "notes": true,
                "gid": true
            };

            _.each(body.data, function(data, key){

                if(dates[key]){
                    if(data){
                        parsed[key] = utils.libDate.parseJSDateTimeToStandardString(new Date(data));
                    }
                } else if(key === "workspace"){
                    if(data){
                        _.each(data, function(wData, wKey){
                            if(wKey == "gid")
                                parsed["workspace_id"] = wData;
                            else
                                parsed["workspace_" + wKey] = wData;
                        });
                    }
                } else if(key === "tags"){

                    if(data && data.length){
                        var tags = "";
                        data.forEach(function(tag) {
                            tags += tag.name + ", "
                        });
                        parsed[key] = tags.slice(0, tags.length - 2);
                    }

                } else if(key === "parent"){
                    if(data && typeof data === 'object' && data.constructor === Object){
                        _.each(data, function(wData, wKey){
                            if(wKey == "gid")
                                 parsed["parent_id"] = wData;
                            else
                                 parsed["parent_" + wKey] = wData;
                        });
                    }
                    parsed["parent"] = data
                } else if (key === 'assignee') {
                    if (data && data.name) {
                        parsed[key] = data.name
                    }
                } else if(keys[key]){
                    if(data !== null && data !== undefined){
                        if(key == 'gid')
                             parsed['id'] = data
                         else    
                            parsed[key] = data;
                    }
                }
            });

            retObj.status = "SUCCESS";
            retObj.data = {
                newObject: {
                    response: {
                        params: parsed
                    }
                },
                hash: {
                    id: body.data.id || body.data.id
                }
            }

            callback(retObj);
            return;

    } else {
        let code
        if (params.response.statusCode === 401) {
            code = 'ERROR_AUTH_REFRESH'
        } else if (params.response.statusCode === 400) {
            code = 'ERROR_MISSING_PARAM'
        } else if (params.response.statusCode === 403 || params.response.statusCode === 429) {
            code = 'ERROR_RATE_LIMIT'
        } else {
            code = 'ERROR_UNKNOWN'
        }
        return callback({
            error: {
                code,
                message: body.error || params.response.statusCode
            },
            errorCode: code,
            status: 'ERROR'
        })
    }
}

source.objectRequest:
function objectRequest(params, utils, callback) {

    if (!params || !params.userapp || !params.appinfo) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'Userapp or appinfo is missing'
            },
            status: 'ERROR'
        })
    }
    if (!params.parsedData || !params.parsedData.data) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'pass parsed Data from pollParse'
            },
            status: 'ERROR'
        })
    }
    const authParams = params.userapp.fetchAuthParams(params.appinfo)
    if (!authParams || !authParams.access_token) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'cannot fetch access_token from fetchAuthParams'
            },
            status: 'ERROR'
        })
    }
    const url = 'https://app.asana.com/api/1.0/tasks'
    const parsedData = params.parsedData.data.objects || []
    const options = parsedData
        .filter(p => p && p.gid)
        .map(p => ({
            headers: {
                Authorization: `Bearer ${authParams.access_token}`
            },
            method: 'GET',
            url: `${url}/${p.gid}`
        }))

    return callback({
        params: JSON.stringify(params),
        status: 'SUCCESS',
        message: 'Built options for request',
        data: { options }
    })
}

