function intialPollParse(hashedParams, hashedUtils, callback) {
  

    var retObj = {};
    var responseObjArray = [];
    // Input Validation
    if (!hashedParams || !hashedParams['body'] || !hashedParams['response']) {

        retObj['status'] = "ERROR";
