        callback(retObj);
                return;
    } else {
        var response = hashedParams['response'];
        var data = [];
        var _ = hashedUtils._;
        var newPolledIds = [];
        var newObjects = []

        if (response['statusCode'] === 200) {

            var body = hashedParams["body"];

            if(!(body || {}) || !(body.data || {}) ){
                retObj["status"] = "ERROR";
                retObj["error"] = {};
                retObj["error"]["message"] = "Missing tasks from body";
                callback(retObj);
                return; 
            }

            //check for any data at all
            if (!body.data.length) {

                retObj['status'] = "SUCCESS";
                retObj['data'] = {};
                retObj['data']['objects'] = [];
                retObj['data']['pollData']  = (hashedParams.workflow.trigger || {}).pollData;
                callback(retObj);
                return;
            }


            //backward compatibility check
            var pollCount = hashedParams.workflow.trigger.pollData.data.count
            //if needs to be made backward compatible
            if (pollCount || pollCount === 0) {

                for (let i = 0 ; i < body.data.length ; i++) {
                    if (i === 3) 
                        break;
                    newPolledIds.push(body.data[i].id || body.data[i].gid)
                }

                if (body.data.length > pollCount) {

                    var newData = body.data.slice(pollCount, body.data.length);

                    retObj["status"] = "SUCCESS";
                    retObj["data"] = {};
                    retObj["data"]["pollData"] = {
                        status: "SUCCESS",
                        data: {
                            polledIds: newPolledIds
                        }
                    };
    
                    retObj["data"]["objects"] = newData;
    

                } else {
                    retObj['status'] = "SUCCESS";
                    retObj['data'] = {};
                    retObj['data']['objects'] = [];
                    retObj['data']['pollData']  = {
                        status: "SUCCESS",
                        data: {
                            polledIds: newPolledIds
                        },
                        forceSave: true
                    }
                }
                callback(retObj);
                return;
            }//end for backward compatibility



            var polledIds = hashedParams.workflow.trigger.pollData.data.polledIds;
            var index = null;

            //finding if one of our saved ids is present in data
            if (polledIds[0]) {
                index = _.findWhere(body.data, {gid: polledIds[0]});
            }
            if (!index && polledIds[1]) {
                index = _.findWhere(body.data, {gid: polledIds[1]});
            }
            if (!index && polledIds[2]) {
                index = _.findWhere(body.data, {gid: polledIds[2]});
            }
            if (!index) {
                index = null
            }

            //if saved id is present then slicing data before the id
            if (index) {

                index = _.findLastIndex(body.data, {gid: index.gid})

                var newTasks = body.data.slice(0, index);

                if (newTasks && newTasks.length) {
                    newTasks.forEach(function(task){
                        newObjects.push(task)
                    });
                }

                if (newObjects.length) {
                    for (var i = 0 ; i < body.data.length ; i++) {
                        if (i === 3) 
                            break;
                        newPolledIds.push(body.data[i].gid)
                    }
                }

            }//end for if we find the id
            else {
                //if all three saved ids are missing from data, then generate three new ids
                for (var i = 0 ; i < body.data.length ; i++) {
                    if (i === 3) 
                        break;
                    newPolledIds.push(body.data[i].gid)
                }

                retObj['status'] = "SUCCESS";
                retObj['data'] = {};
                retObj['data']['objects'] = [];
                retObj['data']['pollData']  = {
                    status: "SUCCESS",
                    data: {
                        polledIds: newPolledIds
                    },
                    forceSave: true
                }
                callback(retObj)
                return

            }//else we generate three new ids and force save them as new poll data 

            //if newObjects array has some new data then we return success
            if(newObjects.length){
            
                retObj["status"] = "SUCCESS";
                retObj["data"] = {};
                retObj["data"]["pollData"] = {
                    status: "SUCCESS",
                    data: {
                        polledIds: newPolledIds
                    }
                };

                retObj["data"]["objects"] = newObjects;

            } else {

                retObj['status'] = "SUCCESS";
                retObj['data'] = {};
                retObj['data']['objects'] = [];
                retObj['data']['pollData']  = (hashedParams.workflow.trigger || {}).pollData;

            }//else saving the old poll data

            

            callback(retObj);
            return
        
            
        // add more error code specific code blocks
    } else if (hashedParams.response.statusCode == 401) {
    
            var code = "ERROR_AUTH_REFRESH";
            retObj.status = "ERROR";
            retObj.errorCode = code;
            retObj.error = {
              code,
              message: "error occured due to expiration of token"
            };
            callback(retObj);
            return
            
    } else if (hashedParams.response.statusCode === 403){

            retObj.status = "ERROR";
            retObj.errorCode = "Forbidden";
            retObj.error = {
                message: hashedParams.body.errors[0].message
            };
            callback(retObj);
            return;

        }  else if (hashedParams.response.statusCode === 404){

