function webhookTriggerParse(hashedParams, hashedUtils, callback){
    
    var retObj = {};

    var _ = hashedUtils._;

     if(hashedParams.request.headers["x-hook-secret"] && _.isEmpty(hashedParams.request.body)){

        retObj.status = "SKIP";
        retObj.message = "Handshake";
        retObj.response = {};
        retObj.response.headers = {
            "x-hook-secret": hashedParams.request.headers["x-hook-secret"]
        };

        callback(retObj);
        return;

    } else if(hashedParams["body"].events.length) {

        var resource_id = _.findWhere(hashedParams.workflow.trigger.params, {
            id: "resource_id"
        });

        resource_id = resource_id.value;

        var task = null;

        hashedParams.body.events.forEach(function(event){
            if(event.resource.resource_type === "task" && event.action === "added" && event.parent.gid == resource_id){
                task = event;
            }
        });

        if(task){

            retObj.data = {response: task};
            retObj.status = "SUCCESS";
            callback(retObj);
            return
            
        } else{

            retObj.status = "SKIP";
            retObj.message = "Unwanted Resource"; 
            callback(retObj);
            return;
        }
        
        
    } else if(hashedParams["body"].events.length === 0) {

        retObj.status = "SKIP";
        retObj.message = "TEST PING";

        callback(retObj);
        return;

    } else {
        retObj.status = "ERROR";
        retObj.error = {
            message: "Error Occured"
        }
        callback(retObj);
        return
    }

}

