function dynamicValueSourceRequest(params, utils, callback) {

    var retObj = {};

    if (!params || !params['appinfo'] || !params['userapp']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";

    } else {

        var appinfo = params['appinfo'];
        var userapp = params['userapp'];

        // Generate HTTP Request body

        var fetchedParams = userapp.fetchAuthParams(appinfo);

        var loginInstanceUrl = fetchedParams['instance_url'];
        var REST_ENDPOINT = "/services/data" ;
        var API_VERSION = fetchedParams['apiVersion'] || '/v39.0'
        const LIMIT = fetchedParams['loadMoreLimit'] && parseInt(fetchedParams['loadMoreLimit']) || 10
        let offset = 0

        if (params.query && params.query.offset) {
            offset = params.query.offset
            retObj.meta = {
                nextPage: {
                  offset: parseInt(offset)
                }
            }
        }

        var options = {
            // Variables to be modified
            method: 'GET',
            url: `/query/?q=SELECT+id, name+FROM+USER+LIMIT+${LIMIT}+OFFSET+${offset}`,

            // Remain same
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization' : 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: loginInstanceUrl + REST_ENDPOINT + API_VERSION,
            json: true
        };

        retObj.step = { limit: LIMIT }

        // Form object to be returned
        retObj['status'] = "SUCCESS";
        retObj['data'] = {
            options: options
        };
    }

    callback(retObj);

}

