        Object.keys(userInput).forEach(function (key) {

            if (key.split("@").length > 1) {

                dataTypeArray = key.split("@");
                dataType = dataTypeArray.pop();

                if (dataType === "date" || dataType === "datetime") {

                    // checking if the input value is valid date or not
                    if (moment(userInput[key]).isValid())
                        sanitizedValue = moment(userInput[key]).toISOString();
                    else
                    // INFO : just for now testing the behaviour of API
                        sanitizedValue = userInput[key];

                } else if (dataType === "boolean") {

                    sanitizedValue = (userInput[key] === "true");

                } else if (dataType === "multipicklist") {
                    let tempArr = [];
                    if (userInput[key] instanceof Array) {
                        userInput[key].forEach(v => {
                            tempArr.push(v.value);
                        });
                    } else
                        tempArr.push(userInput[key])

                    sanitizedValue = tempArr.join('; ');
