function dynamicInputFieldsParse(params, utils, callback) {

    var retObj = { status: "ERROR" };
    var body;
    var response;
    var errorMsg;
    var fields;
    var update;

    if (!params || !params['response'] || !params['body']) {

        retObj['error'] = {};
        retObj['errorCode'] = "ERROR_UNKNOWN";
        retObj['error']['message'] = "Error: Missing response or body.";

    } else {

        response = params['response'];
        body = response['body'];
        update = params.step && params.step.update ? params.step.update : null;

        if (response.statusCode == 200) {

            retObj['status'] = "SUCCESS";
            retObj['data'] = [];

            fields = body.fields ? body.fields : [];
            let customField;
            let type;

            fields.map(function (el) {
                customField = {};

                // if the field is custom field
                if ((el.custom === true && !el.autoNumber)) {

                    type = el.type;
                    customField['id'] = el.name + "@" + type

                    customField['name'] = el.label
                    customField['defaultValue'] = el.defaultValue
                    customField['type'] = type && (type === "date" || type === "datetime") ? "Date" : "String"
                    customField['description'] = el.inlineHelpText

                    // Using few fields to infer if they are required or not
                    if (el.createable && !el.defaultedOnCreate && !el.nillable)
                        customField['required'] = true
                    else
                        customField['required'] = false

                    // exception for few fields
                    if (el.name === "Status" || el.name === "Origin") 
                        customField['required'] = true

                    if (type) {

                        // adding options for the dropdown type fields
                        if (type === "picklist" || type === "boolean" || type === "multipicklist") {

                            if (type === "picklist" || type === "boolean")
                                customField['inputType'] = "select";
                            else
                                customField['inputType'] = "multiselect";

                            customField['fixedValueSource'] = [];

                            if (type === "boolean") {

                                // checkbox can take 2 values either true or false
                                customField['fixedValueSource'].push({ id: "true", name: "Yes" });
                                customField['fixedValueSource'].push({ id: "false", name: "No" });

                            } else {

                                // options for picklist
                                el.picklistValues.forEach(op => {

                                    // if the value is active
                                    if (op.active) {
                                        customField['fixedValueSource'].push({
                                            "id": op.value || op.label,
                                            "name": op.label
                                        });
                                    }
                                }, this);
                            }

                        } else {

                            customField['inputType'] = "input";
                            // adding reference / lookup fields
                            if (type === "reference") {

                                if (el.referenceTo && el.referenceTo.length && el.referenceTo[0]) {

                                    // lookup / reference fields
                                    switch (el.referenceTo[0]) {
                                        case "Contact":
                                            customField['searchAction'] = "search-contact";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Account":
                                            customField['searchAction'] = "search-account";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Lead":
                                            customField['searchAction'] = "search-lead";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Opportunity":
                                            customField['searchAction'] = "search-opportunity";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Task":
                                            customField['searchAction'] = "search-task";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Case":
                                            customField['searchAction'] = "search-case";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "Event":
                                            customField['searchAction'] = "search-event";
                                            customField['searchID'] = "Id";
                                            break;
                                        case "User":
                                            customField['searchAction'] = "search-user";
                                            customField['searchID'] = "Id";
                                    }
                                }
                            } else if (type === "textarea")
                                customField['inputType'] = "textArea";
                        }
                    }
                    retObj['data'].push(customField);
                } 
            }, this);

            const recordTypes = (body.recordTypeInfos || []).filter(type => type.available && !type.master)
            if (recordTypes && recordTypes.length) {
                let defaultOpt
                let field = { 
                    type: 'String',
                    inputType: 'select',
                    name: 'Record Type',
                    required: true,
                    id: 'recordTypeId',
                    fixedValueSource: []
                }

                // options for picklist
                recordTypes.forEach(op => {
                    defaultOpt = op.defaultRecordTypeMapping === true ? op.recordTypeId : defaultOpt
                    field['fixedValueSource'].push({
                        'id': op.recordTypeId,
                        'name': op.name
                    })
                })
                field.defaultValue = defaultOpt || undefined
                retObj['data'].push(field)
            }

        } else {

            let rateErrorOccurred = false;
            errorMsg = "";
            if (body instanceof Array && body.length) {
                body.forEach(err => {
                    if (err.errorCode === "REQUEST_LIMIT_EXCEEDED")
                        rateErrorOccurred = true;
                    // if error code is present
                    errorMsg += err.errorCode ? err.errorCode + ": " : "";
                    // if error msg is present
                    errorMsg += err.message ? err.message : "";
                }, this);
            }

            if (response.statusCode == 401) {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_AUTH_REFRESH";
                retObj['error']['message'] = errorMsg || "Error occured due to expiration of token";
                return callback(retObj);

            } else if (response.statusCode == 403) {

                retObj['error'] = {};
                retObj['errorCode'] = rateErrorOccurred ? "ERROR_RATE_LIMIT" : "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Salesforce api not enabled for your account, please contact your salesforce admin/support";
                return callback(retObj);

            } else {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Error with status code " + response['statusCode'];
            }
        }
    }
    callback(retObj);

}

actions.actionRequest:
function actionRequest(params, utils, callback) {

    var retObj = {};
    var update;
    var moment;
    var entity = "Case";
    const stepValueSearch = "SEARCH_BY_FIELD";
    const stepValueUpdate = "UPDATE_BY_ID";
    const stepValueFetchObj = "FETCH_COMPLETE_OBJECT"

    /**
     * Sanitizes the user input
     * @param {Object} userInput
     * @param {Object} stepData
     */

    var sanitizeUserInput = function (userInput, stepData) {

        let dataType
        let sanitizedValue
        let dataTypeArray
        let skipFields = stepData && stepData.skipFields ? stepData.skipFields.split(',') : []

        delete userInput['Update'];
        delete userInput['value'];
        delete userInput['field'];
        delete userInput['hardUpdateRecord']

        // sanitize the user inputs
        Object.keys(userInput).forEach(function (key) {

            if (key.split("@").length > 1) {

                dataTypeArray = key.split("@");
                dataType = dataTypeArray.pop();

                if (dataType === "date" || dataType === "datetime") {

                    // checking if the input value is valid date or not
                    if (moment(userInput[key]).isValid())
                        sanitizedValue = moment(userInput[key]).toISOString();
                    else
                        // INFO : just for now testing the behaviour of API
                        sanitizedValue = userInput[key];

                } else if (dataType === "boolean") {

                    sanitizedValue = (userInput[key] === "true");

                } else if (dataType === "multipicklist") {
                    let tempArr = [];
                    if (userInput[key] instanceof Array) {
                        userInput[key].forEach(v => {
                            tempArr.push(v.value);
                        });
                    } else
                        tempArr.push(userInput[key])

                    sanitizedValue = tempArr.join('; ');
                } else {
                    sanitizedValue = userInput[key];
                }

                // finally setting the sanitizedValue of the input value
                userInput[dataTypeArray.join('')] = sanitizedValue;
                delete userInput[key];
            }

            if (key === 'CloseDate') {
                // checking if the input value is valid date or not
                if (moment(userInput[key]).isValid())
                    userInput[key] = moment(userInput[key]).toISOString();
            }

        }, this);

        if (userInput['IsPrivate'])
            userInput['IsPrivate'] = (userInput['IsPrivate'] === "Yes");

        skipFields.forEach(field => delete userInput[field])
    }

    if (!params || !params['appinfo'] || !params['userapp'] || !params['params'] || !utils['moment']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";

    } else {

        let userInput = params['params']
        let update = userInput['Update'] ? userInput['Update'] : "No"
        let hardUpdateRecord = userInput['hardUpdateRecord'] ? (userInput['hardUpdateRecord'] === "Yes") : true
        moment = utils['moment']

        let fetchedParams = params['userapp'].fetchAuthParams(params['appinfo']);
        let loginInstanceUrl = fetchedParams['instance_url'];
        let REST_ENDPOINT = "/services/data";
        let API_VERSION = fetchedParams['apiVersion'] || '/v39.0';

        // common options Variables
        let options = {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: loginInstanceUrl + REST_ENDPOINT + API_VERSION,
            json: true
        };

        if (update === 'No' || (params['step'] && params['step']['value'] === stepValueUpdate)) {

            options['method'] = 'POST';
            options['url'] = '/sobjects/' + entity + '/';

            // sanitizes the userInput
            sanitizeUserInput(userInput, params['step'])

            if (update === 'Yes' && (params['step'] && params['step']['caseId'])) {
                options.method = "PATCH";
                options.url = "/sobjects/" + entity + "/" + params['step']['caseId'];
                retObj['step'] = { caseId: params['step']['caseId'] }
            }

            // now add the body to the request
            options['body'] = userInput;

        } else if (params['step'] && params['step']['value'] === stepValueFetchObj) {

            if (params['step']['caseId']) {
                // send the request for fetching whole object corresponding to given id
                options.method = "GET"
                options.url = `/sobjects/Case/${params['step']['caseId']}`
                retObj['step'] = { value: stepValueFetchObj, caseId: params['step']['caseId'] }
            } else {
                options['method'] = 'POST'
                options['url'] = '/sobjects/' + entity + '/'
                // sanitizes the userInput
                sanitizeUserInput(userInput, params['step'])
                 // now add the body to the request
                options['body'] = userInput
            }

        } else if (update === 'Yes') {

            let matchField = userInput['field'];
            let url = "/query/?q=select+id+from+" + entity + "+where+";
            let matchfieldSplit;
            let matchFieldType;

            // if user selected update but field and value not available throw error
            if (!userInput['field'] || !userInput['value']) {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_MISSING_PARAM";
                retObj['error']['message'] = "Missing input for 'Value to check for'. This is required if 'Update if existing' is Yes.";
                return callback(retObj);
            }

            // if the selected matching field is custom field, extract its data type from name
            if (userInput['field'].split('@').length > 1) {

                matchfieldSplit = userInput['field'].split('@');
                matchFieldType = matchfieldSplit.pop();
                matchField = matchfieldSplit.join('');
            }

            // format the query according to the coming field Type
            if ((matchField.includes("Date") || matchField.includes("date") || matchField === "SystemModstamp") || (matchFieldType && (matchFieldType === "date" || matchFieldType === "datetime"))) {

                if (moment(userInput['value']).isValid())
                    userInput['value'] = moment(userInput['value']).toISOString();

                url += matchField + "=" + userInput['value'];
            }
            else
                url += matchField + "='" + encodeURIComponent(userInput['value'].replace(/\\/g, '\\\\').replace(/'/g, '\\\'')) + "'";

            options['method'] = 'GET';
            options['url'] = url;
            retObj['step'] = { value: stepValueSearch, hardUpdateRecord: hardUpdateRecord }
        }

        // Form object to be returned
        retObj['status'] = "SUCCESS";
        retObj['data'] = {
            options: options
        };
    }
    callback(retObj);

}

