function actionParse(params, utils, callback) {

    var retObj = {status: "ERROR"};
    var errorMsg;
    const stepValueUpdate = "UPDATE_BY_ID"
    const stepValueSearch = "SEARCH_BY_FIELD"
    const stepValueFetchObj = "FETCH_COMPLETE_OBJECT"


    if (!params || !params['response']) {

        retObj['error'] = {};
        retObj['errorCode'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";

    } else {

        var response = params['response'];
        var body = response['body'];
        if (response['statusCode'] == 201) {

            var id = body.id;
            retObj['status'] = "SUCCESS";
            retObj['data'] = {
                parsedValues: {
                    id: id
                }
            };

        } else if (response['statusCode'] == 200) {

            let contactId = body && body.records && body.records[0] && body.records[0].Id ? body.records[0].Id : "";
            if (params['step']['value'] === stepValueSearch) {

                retObj['status'] = "SUCCESS"

                if (params['step']['hardUpdateRecord'])
                    retObj['step'] = { value: stepValueUpdate , contactId: contactId }
                else
                    retObj['step'] = { value: stepValueFetchObj , contactId: contactId }

            } else if (params['step']['value'] === stepValueFetchObj) {

                let skip = []
                Object.keys(body).forEach(field => {
                    if (body[field] !== "" && body[field] != null)
                        skip.push(field)
                }, this);

                retObj['status'] = "SUCCESS"
                retObj['step'] = { value: stepValueUpdate, contactId: params['step']['contactId'], skipFields: skip.join(',') }
            }

        } else if (response['statusCode'] == 204) {

            retObj['status'] = "SUCCESS";
            retObj['data'] = {
                parsedValues: {
                    id: params['step'] && params['step']['contactId'] ? params['step']['contactId'] : ""
                }
            };

        } else {

            let rateErrorOccurred = false;
            errorMsg = "";
            if (body instanceof Array && body.length) {
                body.forEach(err => {
                    if (err.errorCode === "REQUEST_LIMIT_EXCEEDED")
