                    // using step to fetch complete data for the object
                    retObj['step'] = { value: FETCH_OBJECT_BY_ID, OpportunityId: body[0]['OpportunityId'], others: body.length > 1 ?  body.slice(1, body.length).map(op => op.OpportunityId).join() : [] }
                else
                    retObj['data'] = { parsedValues: { found: "No" } }

            } else if (step && step.value && step.value === FETCH_OBJECT_BY_ID && body) {

                let responseObject = { found: "Yes" };
