function actionRequest(params, utils, callback) {

    var retObj = {};
    var moment;
    var searchStep = "SEARCH";
    var fetchObjectStep = "FETCH_COMPLETE_OBJECT";
    var url = "/query/?q=SELECT+id+FROM+Lead+WHERE+";

    // if the step data is not there set it to default 
    if (!params['step'])
        params['step'] = { value: searchStep }

    if (!params || !params['appinfo'] || !params['userapp'] || !params['params']) {

        retObj['status'] = "ERROR";
        retObj['error'] = {};
        retObj['error']['code'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";

    } else {

        var appinfo = params['appinfo'];
        var userapp = params['userapp'];
        var userInput = params['params'];
        var searchField = userInput['field'];
        var fetchedParams = userapp.fetchAuthParams(appinfo);
        moment = utils['moment'] ? utils['moment'] : null;

        // checking for utils
        if (!moment) {

            retObj['status'] = "ERROR";
            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);
        }

        // checking if the user input is present or not
        if (!searchField || !userInput['value']) {

            retObj['status'] = "ERROR";
            retObj['error'] = {};
            retObj['error']['code'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required user input";
            return callback(retObj);
        }

        var loginInstanceUrl = fetchedParams['instance_url'];
        var REST_ENDPOINT = "/services/data";
        var API_VERSION = fetchedParams['apiVersion'] || '/v39.0';
        var stepVal = params['step'] && params['step']['value'] ? params['step']['value'] : null;
        let matchfieldSplit;
        let matchFieldType;

        // if the selected matching field is custom field, extract its data type from name
        if (searchField.split('@').length > 1) {

            matchfieldSplit = searchField.split('@');
            matchFieldType = matchfieldSplit.pop();
            searchField = matchfieldSplit.join('');
        }

        // format the query according to the coming field Type
        if (searchField.includes("Date") || searchField.includes("date") || searchField === "SystemModstamp" || (matchFieldType && (matchFieldType === "date" || matchFieldType === "datetime"))) {

            if (moment(userInput['value']).isValid())
                userInput['value'] = moment(userInput['value']).toISOString();
            url += searchField + "=" + userInput['value'];
        }
        else
            url += searchField + "='" + encodeURIComponent(userInput['value'].replace(/\\/g,'\\\\').replace(/'/g,'\\\'')) + "'";

        // common request object
        var options = {

            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: loginInstanceUrl + REST_ENDPOINT + API_VERSION,
            json: true
        };

        if (stepVal && stepVal === searchStep) {

            // searching for the given field and corresponding value
            options['url'] = url;
            retObj['step'] = { value: searchStep };

        } else if (stepVal && stepVal === fetchObjectStep && params['step']['id']) {

            // fetching complete object for the given id
            options['url'] = `/sobjects/Lead/${params.step.id}`;
            retObj['step'] = { value: fetchObjectStep };

        } else {

            retObj['status'] = "ERROR";
            retObj['error'] = {};
            retObj['error']['code'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Unknown error while creating request";
            return callback(retObj);
        }

        // Form object to be returned
        retObj['status'] = "SUCCESS";
        retObj['data'] = {
            options: options
        };
    }

    callback(retObj);

}

