        var libDate = utils['libDate'] ? utils['libDate'] : null;

        if (!libDate) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);
        }

        if (response.statusCode == 200) {

            retObj['status'] = "SUCCESS";

            if (step && step.value && step.value === searchStep) {

                body = response['body'].records ? response['body'].records : [];

                if (body && body.length && body[0] && body[0]['Id'])
                    // using step to fetch complete data for the object
                    retObj['step'] = { value: fetchObjectStep, id: body[0]['Id'] }
                else
                    retObj['data'] = { parsedValues: { found: "No" } }

            } else if (step && step.value && step.value === fetchObjectStep && body) {

                let responseObject = { found: "Yes" };
                Object.keys(body).forEach(field => {

                    if (typeof body[field] != "object" && field != "attributes") {
                        if (field.includes("Date") || field.includes("date") || field === "SystemModstamp")
                            responseObject[field] = body[field] ? libDate.parseJSDateTimeToStandardString(new Date(body[field])) : "";
                        else
                            responseObject[field] = body[field];
                    }
                }, this);

                retObj['data'] = { parsedValues: responseObject }

            } else {

                retObj['status'] = "ERROR";
                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_MISSING_PARAM";
                retObj['error']['message'] = "Error: Unknown error while creating request";
                return callback(retObj);
