                        rateErrorOccurred = true;
                    // if error code is present
                    errorMsg += err.errorCode ? err.errorCode + ": " : "";
                    // Hide actual query from user if malformed query
                    if (err.errorCode && err.errorCode === 'MALFORMED_QUERY')
                        errorMsg += "Kindly check the search criterion."
                    else 
                        errorMsg += err.message ? err.message : "";
                }, this);
            }

            if (response.statusCode == 401) {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_AUTH_REFRESH";
                retObj['error']['message'] = errorMsg || "Error occured due to expiration of token";
                return callback(retObj);

            } else if (response.statusCode == 403) {

                retObj['error'] = {};
                retObj['errorCode'] = rateErrorOccurred ? "ERROR_RATE_LIMIT" : "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Salesforce api not enabled for your account, please contact your salesforce admin/support";
                return callback(retObj);

            } else {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Error with status code " + response['statusCode'];
            }
        }
    }
    callback(retObj);
}

