        retObj['error'] = {}
        retObj['error']['code'] = "ERROR_MISSING_PARAM"
        retObj['error']['message'] = "Error: Input parameters are missing."

    } else {

        let userInput = params['params']
        let searchField = userInput['field']
        let fetchedParams = params['userapp'].fetchAuthParams(params['appinfo'])
        moment =  utils['moment']

        // checking if the user input is present or not
        if (!searchField || !userInput['value']) {

            retObj['status'] = "ERROR"
            retObj['error'] = {}
            retObj['error']['code'] = "ERROR_MISSING_PARAM"
            retObj['error']['message'] = "Missing input for 'Value' to search for."
            return callback(retObj)
        }

        var loginInstanceUrl = fetchedParams['instance_url']
        var REST_ENDPOINT = "/services/data"
        var API_VERSION = fetchedParams['apiVersion'] || '/v39.0';
        var stepVal = params['step'] && params['step']['value'] ? params['step']['value'] : null
        let matchfieldSplit
        let matchFieldType

        // if the selected matching field is custom field, extract its data type from name
        if (searchField.split('@').length > 1) {

            matchfieldSplit = searchField.split('@')
            matchFieldType = matchfieldSplit.pop()
            searchField = matchfieldSplit.join('')
        }

        // format the query according to the coming field Type
        if (searchField.includes("Date") || searchField.includes("date") || searchField === "SystemModstamp" || (matchFieldType && (matchFieldType === "date" || matchFieldType === "datetime"))) {

            if (moment(userInput['value']).isValid())
                userInput['value'] = moment(userInput['value']).toISOString()
            url += searchField + "=" + userInput['value']
        }
        else
            url += searchField + "='" + encodeURIComponent(userInput['value'].replace(/\\/g,'\\\\').replace(/'/g,'\\\'')) + "'"

        // common request object
        var options = {

            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: loginInstanceUrl + REST_ENDPOINT + API_VERSION,
            json: true
        };

        if (stepVal && stepVal === searchStep) {

            // searching for the given field and corresponding value
            options['url'] = url
            retObj['step'] = { value: searchStep }

        } else if (stepVal && stepVal === fetchObjectStep && params['step']['id']) {

            // fetching complete object for the given id
            options['url'] = `/sobjects/User/${params.step.id}`
            retObj['step'] = { value: fetchObjectStep }

        } else {

            retObj['status'] = "ERROR"
            retObj['error'] = {}
            retObj['error']['code'] = "ERROR_MISSING_PARAM"
            retObj['error']['message'] = "Error: Unknown error while creating request"
            return callback(retObj)
        }

        // Form object to be returned
        retObj['status'] = "SUCCESS"
        retObj['data'] = {
            options: options
        }
    }

    callback(retObj)

}

actions.actionParse:
function actionParse(params, utils, callback) {

    var retObj = { status: "ERROR" }
    var errorMsg
    const searchStep = "SEARCH_USER"
    const fetchObjectStep = "FETCH_COMPLETE_OBJECT"


    if (!params || !params['response']) {

        retObj['error'] = {};
        retObj['errorCode'] = "ERROR_MISSING_PARAM";
        retObj['error']['message'] = "Error: Input parameters are missing.";

    } else {

        let response = params['response'];
        let body = response['body'];
        let step = params['step'];
        let libDate = utils['libDate'] ? utils['libDate'] : null;

        if (!libDate) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);
        }

        if (response.statusCode == 200) {

            retObj['status'] = "SUCCESS";

            if (step && step.value && step.value === searchStep) {

                body = response['body'].records ? response['body'].records : [];

                if (body && body.length && body[0] && body[0]['Id'])
                    // using step to fetch complete data for the object
                    retObj['step'] = { value: fetchObjectStep, id: body[0]['Id'] }
                else
                    retObj['data'] = { parsedValues: { found: "No" } }

            } else if (step && step.value && step.value === fetchObjectStep && body) {

                let responseObject = { found: "Yes" };

                Object.keys(body).forEach(field => {
                    if (typeof body[field] != "object" && field != "attributes") {
                        if (field.includes("Date") || field.includes("date") || field === "SystemModstamp")
                            responseObject[field] = body[field] ? libDate.parseJSDateTimeToStandardString(new Date(body[field])) : "";
                        else
                            responseObject[field] = body[field];
                    }
                }, this);

                retObj['data'] = { parsedValues: responseObject }

            } else {

                retObj['status'] = "ERROR";
                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_MISSING_PARAM";
                retObj['error']['message'] = "Error: Unknown error while creating request";
                return callback(retObj);
            }

        } else {

            let rateErrorOccurred = false;
            errorMsg = "";
            if (body instanceof Array && body.length) {
                body.forEach(err => {
                    if (err.errorCode === "REQUEST_LIMIT_EXCEEDED")
                        rateErrorOccurred = true;
                    // if error code is present
                    errorMsg += err.errorCode ? err.errorCode + ": " : "";
                    // Hide actual query from user if malformed query
                    if (err.errorCode && err.errorCode === 'MALFORMED_QUERY')
                        errorMsg += "Kindly check the search criterion."
                    else 
                        errorMsg += err.message ? err.message : "";
                }, this);
            }

            if (response.statusCode == 401) {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_AUTH_REFRESH";
                retObj['error']['message'] = errorMsg || "Error occured due to expiration of token";
                return callback(retObj);

            } else if (response.statusCode == 403) {

                retObj['error'] = {};
                retObj['errorCode'] = rateErrorOccurred ? "ERROR_RATE_LIMIT" : "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Salesforce api not enabled for your account, please contact your salesforce admin/support";
                return callback(retObj);

            } else {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Error with status code " + response['statusCode'];
            }
        }
    }
    callback(retObj);
}

