function authStrategy(hashedParams, hashedUtils, callback) {

  var retObj = {};
  var appInfo = hashedParams.appInfo;

  // Input Params Valdation
  if (!appInfo || !appInfo.auth || !appInfo.auth.authParams || !appInfo.auth.authParams.salesforceEnv) {
    retObj['status'] = "ERROR";
    retObj['error'] = {};
    retObj['error']['code'] = "ERROR_MISSING_PARAM";
    retObj['error']['message'] = "Error: Missing required Environment parameter.";
    return callback(retObj)
  }

  let environment = appInfo.auth.authParams.salesforceEnv
  let site = environment && (environment === "sandbox") ? appInfo.auth.authParams.sandboxSite : appInfo.auth.authParams.site

  var oauth2 = hashedUtils.oauth2({
    clientID: appInfo.auth.authParams.clientID,
    clientSecret: appInfo.auth.authParams.clientSecret,
    scope: ['full', 'refresh_token'],
    prompt: ['login', 'consent'],
    grant_type: "authorization_code",
    site: site,
    tokenPath: appInfo.auth.authParams.tokenPath,
    authorizationPath: appInfo.auth.authParams.authorizationPath
  });

  if (!(hashedUtils || {}).oauth2) {
    retObj.status = 'ERROR';
    retObj.error = { message: 'doesNot have simple-oauth2 object while requesting in authCallback function' };
    callback(retObj);
    return;
  }
  //var oauth2 = hashedUtils.oauth2;
  var authorization_uri = oauth2.authCode.authorizeURL({
    redirect_uri: appInfo.auth.authParams.redirect_uri,
    'access_type': 'offline',
    prompt: "login,consent",
    state: environment && (environment === "sandbox") ? "sandbox" : "production"
  });
  retObj.status = 'SUCCESS';
  retObj.data = { authorization_uri: authorization_uri };
  callback(retObj);
}

auth.authCallBack:
function authCallBack(hashedParams, hashedUtils, callback) {
    var retObj = {}
    var params = []

    if (!((hashedParams || {})).queryObj) {
        retObj.status = 'ERROR'
        retObj.error = { message: 'There was an error while connecting to Salesforce. Please try reconnecting the app to fix this issue. If the issue persists, you can reach out to Salesforce support' }
        callback(retObj)
        return
    }
    var queryObj = hashedParams.queryObj
    if (!(hashedUtils || {}).oauth2) {
        retObj.status = 'ERROR'
        retObj.error = { message: 'There was an error while connecting with Salesforce. Please get in touch with us and we\'ll help you set it up' }
        return callback(retObj)
    }

    if (!hashedParams.queryObj.code) {
        retObj.status = 'ERROR'
        retObj.error = { message: 'There was an error while connecting to Salesforce. Please try reconnecting the app to fix this issue. If the issue persists, you can reach out to Salesforce support' }
        return callback(retObj)
    }

    var appInfo = hashedParams.appInfo
    let environment = hashedParams.queryObj.state ? hashedParams.queryObj.state : 'production'
    let site = environment && (environment === 'sandbox') ? appInfo.auth.authParams.sandboxSite : appInfo.auth.authParams.site

    var oauth2 = hashedUtils.oauth2({
        clientID: appInfo.auth.authParams.clientID,
        clientSecret: appInfo.auth.authParams.clientSecret,
        site: site,
        tokenPath: appInfo.auth.authParams.tokenPath,
        authorizationPath: appInfo.auth.authParams.authorizationPath
    })

    var code = queryObj.code
    oauth2.authCode.getToken({
        code: code,
        redirect_uri: appInfo.auth.authParams.redirect_uri
    }, saveToken)

    function saveToken(ERROR, result) {
        if (ERROR) {
            retObj.status = 'ERROR'
            retObj.error = { message: 'Access Token ERROR' + ERROR }
            return callback(retObj)
        } else {
            if (result && result['access_token'] && result['refresh_token'] && result['instance_url'] && result['id']) {

                // extract orgId from identityUrl
                let identityUrl = result['id']
                let identityUrlParts = identityUrl && identityUrl.split('/') ? identityUrl.split('/') : []
                let orgID = identityUrlParts.length > 2 && identityUrlParts[identityUrlParts.length - 2] ? identityUrlParts[identityUrlParts.length - 2] : null

                params.push({ 'id': 'access_token', value: result['access_token'] })
                params.push({ 'id': 'instance_url', value: result['instance_url'] })
                params.push({ 'id': 'refresh_token', value: result['refresh_token'] })
                params.push({ 'id': 'environment', value: environment })
                params.push({ 'id': 'identity_url', value: result['id'] })
                params.push({ 'id': 'orgId', value: orgID })
                retObj.status = 'SUCCESS'
                retObj.data = { oauth2Info: result, params: params }
                return callback(retObj)
            } else {
                retObj.status = 'ERROR'
                retObj.error = { message: 'Cannot retrieve all the required tokens' }
                return callback(retObj)
            }
        }
    }
}

auth.authRefreshRequest:
function authRefreshRequest(hashedParams, hashedUtils, callback) {
    var retObj = {}
    if (!(hashedParams || {}).userapp || !(hashedParams || {}).appinfo) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'Userapp or appinfo is missing while requesting in dynamicValueSourceRequest'}
        callback(retObj)
        return
    }
    if (!(hashedUtils || {})._) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'hashedUtils doesnot contain underscore module'}
        callback(retObj)
        return
    }
    var _ = hashedUtils._
    var fetchedParams = hashedParams.userapp.fetchAuthParams(hashedParams.appinfo)
    if ((!fetchedParams || {}).access_token) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'cannot fetch access_token from fetchAuthParams'}
        callback(retObj)
        return
    }
    if (!((((hashedParams || {}).appinfo || {}).auth || {}).authParams || {}).clientID) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'appInfo is missing clientId'}
        callback(retObj)
        return
    }
    if (!((((hashedParams || {}).appinfo || {}).auth || {}).authParams || {}).clientSecret) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'appInfo is missing clientSecret'}
        callback(retObj)
        return
    }
    if (!((((hashedParams || {}).appinfo || {}).auth || {}).authParams || {}).sandboxSite) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'appInfo is missing sandboxsite'}
        callback(retObj)
        return
    }
    var refresh_token = _.findWhere(hashedParams.userapp.params, {id: 'refresh_token'})
    var environment = _.findWhere(hashedParams.userapp.params, {id: 'environment'})
    if (!refresh_token || !refresh_token.value) {
        retObj.status = 'ERROR'
        retObj.error = {message: 'refresh token is missing in userApp'}
        callback(retObj)
        return
    }

    const url = environment && (environment.value === 'sandbox') ? hashedParams.appinfo.auth.authParams.sandboxSite : hashedParams.appinfo.auth.authParams.site
    var options = {
        url: `${url}/services/oauth2/token`,
        method: 'POST',
        form: {
            client_id: hashedParams.appinfo.auth.authParams['clientID'],
            client_secret: hashedParams.appinfo.auth.authParams['clientSecret'],
            refresh_token: refresh_token.value,
            grant_type: 'refresh_token'
        }
    }
    retObj.status = 'SUCCESS'
    retObj.data = {options: options}
    callback(retObj)
}

auth.authRefreshParse:
function authRefreshParse(hashedParams, hashedUtils, callback) {
  var retObj = {};
  if(!(hashedParams || {}).response || !(hashedParams || {}).body){
    retObj.status = "ERROR";
    retObj.message = "response or body to parse is missing while requesting in dynamicValueSourceParse";
    callback(retObj);
    return;
  }
  if ((hashedParams.response.statusCode || {}) == 200) {
    hashedParams.body= JSON.parse(hashedParams.body);
    if(!hashedParams.body || !hashedParams.body.access_token){
      retObj.status = "ERROR";
      retObj.message = "Failed to refresh the access_token";
      callback(retObj);
      return;
    }else{
      retObj.status = "SUCCESS";
      retObj.access_token = hashedParams.body.access_token;
      retObj.message = "successfully refreshed access_token";
      callback(retObj);
      return;
    }
  } else {
    retObj.status = "ERROR";
    retObj.message = "error while refreshing the token";
    callback(retObj);
    return;
  }
}

