    } else {

        var response = params['response'];
        var workflow = params['workflow'];
        var body = response['body'];

        var lastPolledDate = (((workflow.trigger || {}).pollData || {}).data || {}).lastDate;
        lastPolledDate = new Date(lastPolledDate);

        moment = utils['moment'] ? utils['moment'] : null;

        if (!moment) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);

        } else if (!lastPolledDate || !lastPolledDate.getTime()) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_PARSE";
            retObj['error']['message'] = "Error: Missing last PolledDate";
            return callback(retObj);
        }

        if (response.statusCode == 200) {

            body = response['body'].records ? response['body'].records : [];
            retObj['status'] = "SUCCESS";

            // if new objects are created
            if (body && body.length) {

                let arrayLength = body.length

                formattedDate = body && body.length && body[arrayLength - 1] ? moment(body[arrayLength - 1].CreatedDate).format('DD-MMM-YYYY HH:mm:ss') : '';
                newLastDate = utils.libDate.parseStandardStringToJSDateTime(formattedDate);

                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': newLastDate } },
                    'objects': body
                };

            } else {
                // if no new objects created
                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': lastPolledDate } },
                    'objects': []
                };
            }
        } else {

