        retObj['error']['message'] = "Error: Missing response or body.";

    } else {

        var response = params['response'];
        var workflow = params['workflow'];
        var body = response['body'];

        var lastPolledDate = (((workflow.trigger || {}).pollData || {}).data || {}).lastDate;

        moment = utils['moment'] ? utils['moment'] : null;

        if (!moment) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);
        }

        if (response.statusCode == 200) {

            body = response['body'].records ? response['body'].records : [];
            retObj['status'] = "SUCCESS";

            // skipping the newly created contacts
            body = body.filter(el => {

                return (el.LastModifiedDate && el.CreatedDate && (new Date(el.LastModifiedDate).getTime() > new Date(el.CreatedDate).getTime()))  
            })

            // if new objects are created
            if (body && body.length) {

                let arrayLength = body.length

                formattedDate = body && body.length && body[arrayLength - 1] ? moment(body[arrayLength - 1].LastModifiedDate).format('DD-MMM-YYYY HH:mm:ss') : '';
