                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': newLastDate } },
                    'objects': body
                };

            } else {
                // if no new objects created
                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': lastPolledDate } },
                    'objects': []
                };
