function objectRequest(params, utils, callback) {

    if (!params || !params.userapp || !params.appinfo) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'Userapp or appinfo is missing'
            },
            status: 'ERROR'
        })
    }
    if (!params.parsedData || !params.parsedData.data) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'pass parsed Data from pollParse'
            },
            status: 'ERROR'
        })
    }

    var fetchedParams = params.userapp.fetchAuthParams(params.appinfo)

    if (!fetchedParams || !fetchedParams.access_token || !fetchedParams['instance_url']) {
        return callback({
            error: {
                code: 'ERROR_MISSING_PARAM',
                message: 'cannot fetch access_token or instance url from fetchAuthParams'
            },
            status: 'ERROR'
        })
    }

    var loginInstanceUrl = fetchedParams['instance_url'];
    var REST_ENDPOINT = "/services/data";
    var API_VERSION = fetchedParams['apiVersion'] || '/v39.0'
    var parsedData = params.parsedData.data.objects || []

    var options = parsedData
        .filter(p => p && p.Id)
        .map(p => ({
            method: 'GET',
            url: `/sobjects/Opportunity/${p.Id}`,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + fetchedParams['access_token']
            },
            baseUrl: loginInstanceUrl + REST_ENDPOINT + API_VERSION,
            json: true
        }))

    return callback({
        params: JSON.stringify(params),
        status: 'SUCCESS',
        message: 'Built options for request',
        data: { options }
    })
}

