    var errorMsg;
    var moment;

    // Input Validation
    if (!params || !params['response'] || !params['body'] || !params['workflow']) {

        retObj['error'] = {};
        retObj['errorCode'] = "ERROR_PARSE";
        retObj['error']['message'] = "Error: Missing response or body.";

    } else {

        var response = params['response'];
        var workflow = params['workflow'];
        var body = response['body'];

        var lastPolledDate = (((workflow.trigger || {}).pollData || {}).data || {}).lastDate;
        lastPolledDate = new Date(lastPolledDate);

        moment = utils['moment'] ? utils['moment'] : null;

        if (!moment) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_MISSING_PARAM";
            retObj['error']['message'] = "Error: Missing required util";
            return callback(retObj);

        } else if (!lastPolledDate || !lastPolledDate.getTime()) {

            retObj['error'] = {};
            retObj['errorCode'] = "ERROR_PARSE";
            retObj['error']['message'] = "Error: Missing last PolledDate";
            return callback(retObj);
        }

        if (response.statusCode == 200) {

            body = response['body'].records ? response['body'].records : [];
            retObj['status'] = "SUCCESS";
            console.log(JSON.stringify(response['body'].records))

            // if new objects are created
            if (body && body.length) {

                let arrayLength = body.length

                formattedDate = body && body.length && body[arrayLength - 1] ? moment(body[arrayLength - 1].CreatedDate).format('DD-MMM-YYYY HH:mm:ss') : '';
                newLastDate = utils.libDate.parseStandardStringToJSDateTime(formattedDate);

                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': newLastDate } },
                    'objects': body
                };

            } else {
                // if no new objects created
                retObj['data'] = {
                    'pollData': { 'status': 'SUCCESS', 'data': { 'lastDate': lastPolledDate } },
                    'objects': []
                };
            }
        } else {

            let rateErrorOccurred = false;
            errorMsg = "";
            if (body instanceof Array && body.length) {
                body.forEach(err => {
                    if (err.errorCode === "REQUEST_LIMIT_EXCEEDED")
                        rateErrorOccurred = true;
                    // if error code is present
                    errorMsg += err.errorCode ? err.errorCode + ": " : "";
                    // if error msg is present
                    errorMsg += err.message ? err.message : "";
                }, this);
            }

            if (response.statusCode == 401) {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_AUTH_REFRESH";
                retObj['error']['message'] = errorMsg || "Error occured due to expiration of token";
                return callback(retObj);

            } else if (response.statusCode == 403) {

                retObj['error'] = {};
                retObj['errorCode'] = rateErrorOccurred ? "ERROR_RATE_LIMIT" : "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Salesforce api not enabled for your account, please contact your salesforce admin/support";
                return callback(retObj);

            } else {

                retObj['error'] = {};
                retObj['errorCode'] = "ERROR_UNKNOWN";
                retObj['error']['message'] = errorMsg || "Error with status code " + response['statusCode'];
            }
        }
    }

    callback(retObj);
}

